import 'dart:convert';
import 'package:flutter/material.dart';

const default_base64Image = '';

class MemoryImageWidget extends StatelessWidget {
  final String image;

  MemoryImageWidget(this.image) {}

  @override
  Widget build(BuildContext context) {
    String imageToPrint = image.isNotEmpty ? image : default_base64Image;
    return Image.memory(
      base64Decode(imageToPrint),
      width: 190,
    );
  }
}
